mapper.select:
/* boardMapper.getListBoard */
SELECT * FROM ( SELECT ROWNUM AS RN ,BOARD_ID ,TITLE ,CONTENTS ,REGT_NM ,REGT_ID ,TO_CHAR(REGT_DATE,'YYYY.MM.DD') AS REGT_DATE ,UPDATE_NM ,UPDATE_ID ,TO_CHAR(UPDATE_DATE,'YYYY.MM.DD') AS UPDATE_DATE ,PARENT_ID ,DEPTH ,GROUP_ID ,ORDER_IN_GROUP FROM SPRINGBOARD WHERE 1 = 1
AND TITLE LIKE ('%'||#{searchWord}||'%')
AND REGT_NM LIKE ('%'||#{searchWord}||'%')
AND UPDATE_NM LIKE ('%'||#{searchWord}||'%')
START WITH PARENT_ID = 0 CONNECT BY PRIOR BOARD_ID = PARENT_ID ORDER SIBLINGS BY ORDER_IN_GROUP ASC, BOARD_ID DESC ) WHERE RN BETWEEN #{startRow} AND #{endRow}

mapper.insert:
/* boardMapper.insertBoard */

	INSERT INTO SPRINGBOARD
	(
		BOARD_ID
		,TITLE
		,CONTENTS
		,REGT_NM
		,REGT_ID
		,REGT_DATE
		,PARENT_ID
		,GROUP_ID
		,DEPTH
		,ORDER_IN_GROUP
	) VALUES (
		#{board_id}
		,#{title}
		,#{contents}
		,#{regt_nm}
		,#{regt_id}
		,SYSDATE
		,#{parent_id}
		,(SELECT COALESCE((SELECT GROUP_ID FROM SPRINGBOARD WHERE BOARD_ID = #{parent_id}),#{board_id})FROM DUAL)
		,#{depth}
		,(SELECT NVL(MAX(ORDER_IN_GROUP),0)+1 FROM SPRINGBOARD WHERE GROUP_ID = COALESCE((SELECT GROUP_ID FROM SPRINGBOARD WHERE BOARD_ID = #{parent_id}),#{board_id}))
	)

mapper.update:
/* boardMapper.updateBoard */

	UPDATE SPRINGBOARD SET
		TITLE = #{title}
		,CONTENTS = #{contents}
		,UPDATE_NM = #{update_nm}
		,UPDATE_ID = #{update_id}
		,UPDATE_DATE = SYSDATE
	WHERE 	BOARD_ID = #{board_id}
